sys_script_include.script:
var LogDataInConsole = Class.create();
LogDataInConsole.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    GetData: function() { //This is executing everything correctly but is not passing the values back to the UI action
        var value = 'User1';
        var grConfigure = new GlideRecord('x_58872_needit_configuration');
        grConfigure.addQuery('username', value);
        grConfigure.query();
        if (grConfigure.next()) {
            var tempName = grConfigure.username;
            return tempName;
        } else {
            gs.info('No Data Selected');
            return false;

        }
    },

    type: 'LogDataInConsole'
});
